import React, { Component } from "react";
import EmpolyerItem from "./EmpolyerItem.js";
import memberInfo from "./memberInfo.js";


class Empolyer extends Component{
    
    constructor(){
        super()
        this.state = { Info: [] }

        this.clickEvent = this.clickEvent.bind(this)
        this.mapping = this.mapping
    }


    clickEvent(){


        this.setState(prevState => {
            return {Info: prevState.Info.push(memberInfo[0])}
        })        
    }





    mapping(x){
        let members;
        
        members = x.map(member =>
                    
            <EmpolyerItem   
                            key={member.id}
                            name={member.name} 
                            nick={member.nick}
                            role={member.role}
                            img={member.img}
                            alt={member.alt}
                            Email={member.Email}
                            mobile={member.mobile}
                            website={member.website} />
            )

        return members
    }

    render(){
  
               

                 return(
                    <div className="row">
                        <button onClick={this.clickEvent} className="btn btn-primary btn-lg btn-block">Click Me!</button>
                        {this.mapping(this.state.Info)}
                    </div>
                )
    }
}
export default Empolyer
