import React, { Component } from "react";
import EmpolyerItem from "./EmpolyerItem.js";

class Empolyer extends Component{
    
    constructor(){
        super()
        this.state = {
                        memo:[
                        {id: 1, name:'NOUR', nick:'HOMSI', role:'Web Developer', img:'https://picsum.photos/id/60/200/300', alt:'pic-empolyee-1', Email: 'homsi@gmail.com', website: 'www.Nourhomsi.com',},
                        {id: 2, name:'NADHIR', nick:'NACEF', role:'Full Stack Web Developer', img:'https://picsum.photos/id/61/200/300', alt:'pic-empolyee-2', mobile: '+213555956198', Email: 'nnacef877@gmail.com', },
                        {id: 3, name:'RAOUF', nick:'ALIM', role:'Mobile Developer', img:'https://picsum.photos/id/62/200/300', alt:'pic-empolyee-3', website: 'www.raouf-alim.dz', mobile: '+213554959280', },
                        {id: 4, name:'MEHDI', nick:'SLIMANI', role:'Desktop Developer', img:'https://picsum.photos/id/63/200/300', alt:'pic-empolyee-4', website: 'www.slimani.fr', Email: 'didoslimani822@gmail.com', },
                        {id: 5, name:'ABDEALI', nick:'GHERDIS', role:'Web Developer', img:'https://picsum.photos/id/64/200/300', alt:'pic-empolyee-5', Email: 'a_gherdis@esi.dz', mobile: '+2137798258962', },
                        {id: 6, name:'Zakaria', nick:'KHERBANENE', role:'Designer', img:'https://picsum.photos/id/65/200/300', alt:'pic-empolyee-6', Email: 'kherbanene822@gmail.com', mobile: '+21377912021', },
                        ]
                    }
    }



    mapping(x){
        let members;
        
        members = x.map(member =>
                    
            <EmpolyerItem   
                            key={member.id}
                            name={member.name} 
                            nick={member.nick}
                            role={member.role}
                            img={member.img}
                            alt={member.alt}
                            Email={member.Email}
                            mobile={member.mobile}
                            website={member.website} />
            )

        return members
    }

    render(){
  
               

                 return(
                    <div className="row">
                        {this.mapping(this.state.p)}
                    </div>
                )
    }
}
export default Empolyer
