import React, { Component } from "react";
import EmpolyerItem from "./EmpolyerItem.js";
import memberInfo from "./memberInfo.js";


class Empolyer extends Component{
    
    constructor(){
        super()
        this.state = { members: [memberInfo[0],], memberInfo: memberInfo , counter: 1}

        this.clickEvent = this.clickEvent.bind(this)
        
    }


    clickEvent(){

        
        
        
        this.newMembers = this.state.members.map(member => 
                    <EmpolyerItem   

                                    key={member.id}
                                    name={member.name} 
                                    nick={member.nick}
                                    role={member.role}
                                    img={member.img}
                                    alt={member.alt}
                                    Email={member.Email}
                                    mobile={member.mobile}
                                    website={member.website} />
        
        )

        this.setState(function(prevState){
            return {members: this.state.members.push(this.state.memberInfo[this.state.counter]),memberInfo: memberInfo,counter: prevState.counter + 1}
        }) 


        

    }


    render(){
  
               

                 return(
                    <div className="row">
                        <button onClick={this.clickEvent} className="btn btn-primary btn-lg btn-block">Click Me!</button>
                        {this.newMembers}
                    </div>
                )
    }
}
export default Empolyer
