import React, { Component } from "react";
import EmpolyerItem from "./EmpolyerItem.js";
import memberInfo from "./memberInfo.js";


class Empolyer extends Component{
    
    constructor(){
        super()
        this.state = { Info: [], memberInfo: memberInfo , counter: 1}

        this.clickEvent = this.clickEvent.bind(this)
        
    }


    clickEvent(){


        this.setState(prevState => {
            return {Info: prevState.Info.push(this.state.memberInfo[prevState.counter]),memberInfo: memberInfo, counter: prevState.counter + 1}
        }) 
        
        this.newMembers = this.state.Info.map(function(member){
                    return(<EmpolyerItem   

                                    key={member.id}
                                    name={member.name} 
                                    nick={member.nick}
                                    role={member.role}
                                    img={member.img}
                                    alt={member.alt}
                                    Email={member.Email}
                                    mobile={member.mobile}
                                    website={member.website} />)
            }
        )




        

    }


    render(){
  
               

                 return(
                    <div className="row">
                        <button onClick={this.clickEvent} className="btn btn-primary btn-lg btn-block">Click Me!</button>
                    </div>
                )
    }
}
export default Empolyer
